-- MySQL dump for safety moments
CREATE DATABASE IF NOT EXISTS safety_moments_db;
USE safety_moments_db;

DROP TABLE IF EXISTS safety_moment;

CREATE TABLE safety_moment (
  id INT AUTO_INCREMENT PRIMARY KEY,
  category VARCHAR(255) NOT NULL,
  message TEXT NOT NULL,
  created_at DATE DEFAULT (CURRENT_DATE)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

